import React, {Fragment, useEffect} from 'react'
import PropTypes from 'prop-types'
import { connect} from 'react-redux'
import Spinner from '../layout/Spinner'
import {getProfileById} from '../../actions/profile'
import { useParams, Link } from 'react-router-dom'
import ProfileTop from './ProfileTop'
import ProfileAbout from './ProfileAbout'
import ProfileExperience from './ProfileExperience'
import ProfileEducation from './ProfileEducation'

const Profile = ({getProfileById, profile: {profile, loading}, auth}) => {
    const {id} = useParams();
    useEffect(() => {
        getProfileById(id);
    },[]);

  return (
    <Fragment>
        {profile === null || loading ? <Spinner /> : <Fragment>
                <Link to="/profiles" className="btn btn-light"> Back to profiles</Link>
                {auth.isAuthenticated && auth.loading === false && auth.user._id === profile.user._id && (<Link to='/edit-profile' className='btn btn-dark'>Edit Profile</Link>)}
                <div class="profile-grid my-1">
                    <ProfileTop profile={profile} />
                    <ProfileAbout profile={profile} />
                    <div className='profile-exp bg-white p-2'>
                        <h2 className='text-primary'>Experience</h2>
                        
                        <p>{profile.experience.description}</p>
                        {profile.experience.length > 0 ? (<Fragment>
                            {profile.experience.map(experience => {
                                return <ProfileExperience key={experience._id} experience={experience}/>
                            })}
                        </Fragment>) : (<h4>No experience credentials</h4>)}
                    </div>
                    <div className='profile-edu bg-white p-2'>
                        <h2 className='text-primary'>Education</h2>
                        
                        <p>{profile.education.description}</p>
                        {profile.education.length > 0 ? (<Fragment>
                            {profile.education.map(education => {
                                return <ProfileEducation key={education._id} education={education}/>
                            })}
                        </Fragment>) : (<h4>No education credentials</h4>)}
                    </div>
                    {profile.githubusername && (
                        <ProfileGithub />
                    )}
                </div>
            </Fragment>}
    </Fragment>
  )
}

Profile.propTypes = {
    getProfileById: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    profile: state.profile,
    auth: state.auth
});

export default connect(mapStateToProps, {getProfileById})(Profile);
