        dispatch({
            type: GET_PROFILE,
            payload: res.data
        });

        dispatch(setAlert(edit ? 'Profile updated' : 'Profile created', 'success')); //set an alert

        if(!edit){ //if we're creating a new profile we get redirected 
            history('/dashboard');
        }
    } catch (err) {
        const errors = err.response.data.errors;

        if(errors){
            errors.forEach(error => dispatch(setAlert(error.msg,'danger')));
        }
        dispatch({
            type: PROFILE_ERROR,
            payload: {msg : err.response.statusText, status:err.response.status}
        });
    }
};

//Add experience
